class searchVC: UIViewController,CLLocationManagerDelegate,UITableViewDelegate,UITableViewDataSource,UISearchBarDelegate{
     var searchCity = [String]()
    var searching = false
  
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
       if searching==true{
            return searchCity.count
        }else{
            return arr.count
        }
      //  return arr.count
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "cell", for: indexPath)as! searchTableViewCell
        if searching==true {
            cell.city.text = searchCity[indexPath.row]
        }else{
            cell.city.text = arr[indexPath.row]
        }
  //  cell.textLabel?.text = arr[indexPath.row]
        return cell
    }
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
         let cell = tableView.dequeueReusableCell(withIdentifier: "cell", for: indexPath)as! searchTableViewCell
        if searching==true {
            cell.city.text = searchCity[indexPath.row]
            location.title = cell.city.text
            
                navigate()
        }else{
            cell.city.text = arr[indexPath.row]
            location.title = cell.city.text
           
            navigate()
            
        }
    }
    func navigate() {
        DispatchQueue.main.asyncAfter(deadline: .now() + 2.0){
            let storyBoard : UIStoryboard = UIStoryboard(name: "Main", bundle:nil)
            let nextViewController = storyBoard.instantiateViewController(withIdentifier: "Home") as! HomeVC
            self.navigationController?.pushViewController(nextViewController, animated: true)}
    }
    func searchBar(_ searchBar: UISearchBar, textDidChange searchText: String) {
        searchCity = arr.filter({$0.prefix(searchText.count) == searchText})
        searching = true
        tableView.reloadData()
    }
    func searchBarCancelButtonClicked(_ searchBar: UISearchBar) {
        searching = false
        search.text = ""
        tableView.reloadData()
    }
    func changeLabel(){
        let vc = HomeVC()
        vc.displayLocation?.title = location.title
    }
  
  
    
